import { ethers } from "ethers";
import JewelryCertificateABI from "./JewelryCertificateABI.json";

export const getContract = () => {
  if (!window.ethereum) {
    throw new Error("MetaMask is not installed. Please install it to use this feature.");
  }
  const provider = new ethers.providers.JsonRpcProvider("http://127.0.0.1:8545"); // Hardhat/Ganache 默认端口
  console.log("Provider Network:", provider.getNetwork());

//   const provider = new ethers.providers.Web3Provider(window.ethereum);
  const signer = provider.getSigner();

  const contractAddress = process.env.REACT_APP_CONTRACT_ADDRESS; // Replace with your deployed contract address
  return new ethers.Contract(contractAddress, JewelryCertificateABI, signer);
};
