import React, { useState } from "react";
import { getContract } from "../contract";

function QueryLogEvents() {
  const [tokenId, setTokenId] = useState("");
  const [logEvents, setLogEvents] = useState([]);
  const [owner, setOwner] = useState("");
  const [status, setStatus] = useState("");

  const fetchLogEvents = async () => {
    if (!tokenId || isNaN(tokenId)) {
      setStatus("Please enter a valid Token ID.");
      return;
    }

    try {
      const contract = await getContract(); // 确保 ABI 和合约地址正确
      console.log("Contract instance:", contract);

      // 获取 tokenId 的所有者
      const tokenOwner = await contract.ownerOf(tokenId);
      setOwner(tokenOwner);

      // 调用 getEvents 方法
      const events = await contract.getEvents(tokenId);
      console.log("Fetched events:", events);

      setLogEvents(events);
      setStatus(`Successfully fetched ${events.length} events for Token ID ${tokenId}`);
    } catch (error) {
      console.error("Error fetching events:", error.message || error);

      // 错误处理提示
      if (error.message.includes("Not authorized")) {
        setStatus("You are not authorized to view events for this Token ID.");
      } else if (error.message.includes("Token does not exist")) {
        setStatus("The specified Token ID does not exist.");
      } else {
        setStatus(`Failed to fetch events: ${error.message || error}`);
      }
    }
  };

  return (
    <div className="component">
      <h2>Query Log Events</h2>
      <input
        type="text"
        placeholder="Token ID"
        value={tokenId}
        onChange={(e) => setTokenId(e.target.value)}
      />
      <button onClick={fetchLogEvents}>Fetch Events</button>
      <p>{status}</p>
      {owner && (
        <p>
          <strong>Owner of Token ID {tokenId}:</strong> {owner}
        </p>
      )}
      {logEvents.length > 0 && (
        <div>
          <h3>Log Events:</h3>
          <ul>
            {logEvents.map((event, index) => (
              <li key={index}>
                <strong>Description:</strong> {event.description} <br />
                <strong>Timestamp:</strong>{" "}
                {new Date(event.timestamp * 1000).toLocaleString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default QueryLogEvents;
